{% extends 'base.html.twig' %}

{% block title %}Réserver un service{% endblock %}

{% block body %}
    <h1 style=\"text-align: center; margin-bottom: 30px;\">Réserver un service</h1>

    {% for message in app.flashes('success') %}
        <div class=\"alert alert-success\" style=\"text-align: center; max-width: 600px; margin: 0 auto 20px;\">
            {{ message }}
        </div>
    {% endfor %}

    {% for message in app.flashes('error') %}
        <div class=\"alert alert-danger\" style=\"text-align: center; max-width: 600px; margin: 0 auto 20px;\">
            {{ message }}
        </div>
    {% endfor %}

    <div class=\"form-container\">
        {{ form_start(form) }}
            {{ form_row(form.date, { 'attr': { 'class': 'form-field' } }) }}
            {{ form_row(form.service, { 'attr': { 'class': 'form-field' } }) }}
        {{ form_end(form) }}
    </div>

{% endblock %}

{% block stylesheets %}
    {{ parent() }}
    <style>
        .form-container {
            max-width: 600px;
            margin: 0 auto;
            padding: 20px;
            background-color: #f8f9fa;
            border-radius: 10px;
            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
        }

        .form-field {
            margin-bottom: 15px;
            border-radius: 5px;
            padding: 10px;
            width: 100%;
        }
        .form-submit {
            display: block;
            width: 100%;
            padding: 10px;
            margin-top: 20px;
            border-radius: 5px;
            font-weight: bold;
        }
    </style>
{% endblock %}
